import React, { useContext } from "react";
import {Link, Route} from "react-router-dom";
import "./styles.css";
import {CartContext} from "../cart/context";
import LogIn from "../../pages/login"

export default function TopBar() {
  const cartCtx = useContext(CartContext);
  const numItems = cartCtx.itemsCount;
  return <div className="layout top-bar">
    <div className="wrapper">
      <div className="section">Логотип</div>
      <div className="section">
        <Route path="/login" exact component={LogIn}>Войти в аккаунт</Route>
      </div>
      <div className="section">
        <Link to="/">Товары</Link> | {" "}
        <Link to="/view-cart">Корзина ({numItems})</Link>
      </div>
    </div>
  </div>
}
